                    <path
                        id="XMLID_49_"
                        className="st1"
                        style={{ fill: '#43d39e' }}
                        d="M77.3,42.7V77c0,0.6-0.4,1-1,1H21.7c-0.5,0-1-0.5-1-1V42.7c0-0.3,0.1-0.6,0.4-0.8l27.3-21.7
