                        id="XMLID_48_"
                        className="st2"
                        style={{
                            fill: '#FFFFFF',
                            stroke: '#43d39e',
                            strokeWidth: 2,
                            strokeMiterlimit: 10,
                        }}
                        d="M66.5,69.5h-35c-1.1,0-2-0.9-2-2V26.8c0-1.1,0.9-2,2-2h35c1.1,0,2,0.9,2,2v40.7
