                        d="M74.9,45.2L49.5,63.5c-0.3,0.2-0.7,0.2-1.1,0L23.2,45.2"
                    />
                </g>
            </g>
        </svg>
    );
};

/* bottom link */
const BottomLink = () => {
    const { t } = useTranslation();
    return (
        <Row className="mt-3">
            <Col xs={12} className="text-center">
                <p className="text-muted">
                    {t('Back to')}{' '}
                    <Link to={'/auth/login'} className="text-primary fw-bold ms-1">
                        {t('Login')}
                    </Link>
                </p>
            </Col>
        </Row>
    );
};

const Confirm = () => {
    const { t } = useTranslation();

    return (
        <>
            <AuthLayout bottomLinks={<BottomLink />}>
                <div className="text-center">
                    <div className="auth-logo mx-auto mb-3">
                        <Link to="/" className="logo logo-dark text-center">
                            <span className="logo-lg">
                                <img src={logoDark} alt="" height="24" />
                            </span>
                        </Link>

                        <Link to="/" className="logo logo-light text-center">
                            <span className="logo-lg">
                                <img src={logoLight} alt="" height="24" />
                            </span>
                        </Link>
                    </div>

                    <Success />
                    <h4 className="h4 mb-2 mt-2">{t('Confirm your email')}</h4>
                    <p className="text-muted mt-1 mb-2">
                        {t(
                            'Your account has been successfully registered. To complete the verification process, please check your email for a validation request.'
                        )}
                    </p>
                </div>
            </AuthLayout>
        </>
    );
};

export default Confirm;
